    lodsb                  ;Carrega em AL o conteudo apontado por SI e inc SI
    cmp ax, cx             ;Verifica se o num de quadrados fechados e o mesmo do de bombas
    jne .end
    jmp youWin

    .end: 
        pop si             ;Recuperar estado anterior dos registradores
        pop dx
        pop cx
        pop bx
        pop ax
        ret                 ;Retornar

